mapper.select:
SELECT count(1) FROM sk_board WHERE flag = #{flag}
AND title LIKE '%' #{searchValue} '%'
AND content LIKE '%' #{searchValue} '%'

SELECT no, lev, user_id AS userId, (select user_nm from auth_member where user_id = A.user_id) AS userNm, title, DATE_FORMAT(reg_date,'%Y.%m.%d %H:%i') AS regDate, read_cnt AS readCnt, (select count(*) from sk_board_file where no = A.no and flag = 'F') AS fileCount, IF(SYSDATE() < DATE_ADD(reg_date, INTERVAL 3 DAY),'1','0') AS isNew FROM sk_board A WHERE flag = #{flag}
AND title LIKE '%' #{searchValue} '%'
AND content LIKE '%' #{searchValue} '%'
ORDER BY ${sortKey} ${sortValue}
ORDER BY fid DESC,stp ASC
LIMIT #{start},#{limit}

mapper.update:
UPDATE sk_board SET 
	title = #{title},
	content = #{content},
	html = #{html},
	ip = #{ip}
WHERE no=#{no}
AND user_id = #{userId}

mapper.select:
SELECT idx, file_name AS fileName, file_real_name AS fileRealName, file_ext AS fileExt, file_size AS fileSize FROM sk_board_file WHERE no = #{no}
AND flag = #{flag}
ORDER BY idx
